function getData(n){try{return document.getElementById("javascript").getAttribute(n)}catch(t){console.error("Couldn't read data from "+n,t)}}function isEmptyOrSpaces(n){return n===null||n.match(/^ *$/)!==null}function shortenUrl(){var n=document.getElementById("inputLongUrl").value;isEmptyOrSpaces(n)&&alert("Invalid long URL.");$.ajax({url:getData("data-linkApiBaseAddress")+"?url="+n,method:"POST",success:function(n){alert("Short URL generated - ["+n+"]")},error:function(n){n.status===400?alert("Invalid URL provided. Please provide an absolute URL."):alert("Fail to generate short URL.")}})}function getLongUrl(){var n=document.getElementById("inputShortUrl").value;isEmptyOrSpaces(n)&&alert("Invalid short URL.");$.ajax({url:getData("data-linkApiBaseAddress")+n,method:"GET",success:function(n){alert("The long URL is ["+n+"]")},error:function(t){t.status===404?alert("Long URL not found for "+n):alert("Failed to get long URL.")}})}$(document).ready(function(){document.getElementById("buttonShortenUrl").addEventListener("click",shortenUrl);document.getElementById("buttonLongUrl").addEventListener("click",getLongUrl)});